		{
			float stdDev = 0;
			float sumAll = 0;
			float sumAllQ = 0;

			//Sum of x and sum of xÂ²
			for (int i = 0; i < data.Length; i++)
			{
				float x = data[i];
				sumAll += x;
				sumAllQ += x * x;
			}

			//Mean (not used here)
			//double mean = 0;
			//mean = sumAll / (double)data.Length;

			//Standard deviation
			stdDev = Mathf.Sqrt(
				(sumAllQ -
					(sumAll * sumAll) / data.Length) *
				(1.0f / (data.Length - 1))
			);

			return stdDev;
		}


