			if (r_val < 0.15f && originalList.Count > 1) {

				for (int i = 0; i < possibleAdditions_shuffled.Count; ++i) {
				
					if (result.Contains (possibleAdditions_shuffled [i])) {
						result.Remove (possibleAdditions_shuffled [i]);
